import toast from 'react-hot-toast';
import { isRouteErrorResponse } from 'react-router-dom';

//validate loginpage username
export async function usernameValidate(values){
    const errors = usernameVerify({}, values);
    return errors;
}
// validate username
function usernameVerify(error={}, values){
    if(!values.username){
        error.username = toast.error('Username Required...!');
    }   else if(values.username.includes(" ")){
        error.username = toast.error("Invalid Username...!");
    }
    return error;
}
export async function passwordValidate(values) {
    const errors = passwordVerify({}, values);
    return errors;
}

// validate Password
(function passwordVerify(errors = {}, values) {
    const specialCharacters = /[@!#$%^&*()_+\-=\[\]{};':"\\|,.<>\/?]/;
    if (!values.password) {
        errors.message = toast.error("Password Required...!");
    } else if (values.password.includes(" ")) {
        errors.message = toast.error("Password should not contain spaces...!");
    } else if (values.password.length < 4) {
        errors.message = toast.error("Password must be more than 4 characters...!");
    } else if (!specialCharacters.test(values.password)) {
        errors.message = toast.error("Password must contain special characters...!");
    }
    return errors;
})();
