import toast from 'react-hot-toast';
import { isRouteErrorResponse } from 'react-router-dom';

//validate loginpage username
export async function usernameValidate(values){
    const errors = usernameVerify({}, values);
    return errors;
}
// validate username
function usernameVerify(error={}, values){
    if(!values.username){
        error.username = toast.error('Username Required...!');
    }   else if(values.username.includes(" ")){
        error.username = toast.error("Invalid Username...!");
    }
    return error;
}
export async function passwordValidate(values) {
    const errors = passwordVerify({}, values);
    return errors;
}
function showToast(message) {
  toast.info(message, {
    autoClose: 1000, // Adjust the time as needed (in milliseconds)
    position: toast.POSITION.TOP_RIGHT
  });
}
// validate Password
function passwordVerify(errors = {}, values) {
    const specialCharacters = "!@#$%^&*()_+-=[]{}|;:'\",.<>?/";
    if (!values.password) {
        errors.password = toast.error("Password Required...!");
    } else if (values.password.includes(" ")) {
        errors.password = toast.error("Wrong Password...!");
    } else if (values.password.length < 4) {
        errors.password = toast.error("Password must be more than 4 characters...!");
    } else if (!RegExp(`[${specialCharacters}]`).test(values.password)) {
        errors.password = toast.error("Password must contain special characters...!");
    }
    return errors;
}
