import React, { useState } from "react";
import { Link } from "react-router-dom";
import avatar from "../assets/profile.png";
import styles from "../styles/Username.module.css";
import { Toaster } from "react-hot-toast";
import { useFormik } from "formik";
import { registerationValidateForm } from "../helper/validate";
import convertToBase64 from "../helper/convert";
const Profile = () => {
  const [file, setFile] = useState();
  const formik = useFormik({
    initialValues: {
      email: "admin@gmail.com",
      username: "waleed",
      password: "admin@123",
    },
    validateOnBlur: false,
    validateOnChange: false,

    onSubmit: async (values) => {
      // Call the passwordValidate function manually before submitting the form
      values = await Object.assign(values, { profile: file || "" });
      const errors = await registerationValidateForm(values);
      if (Object.keys(errors).length === 0) {
        // Proceed with form submission if there are no errors
        console.log(values);
      }
    },
  });
  //  useformik doesnt support the file upload so i create the function for it
  const onUpload = async (e) => {
    const base64 = await convertToBase64(e.target.files[0]);
    setFile(base64);
  };

  return (
    <div className="container mx-auto">

    <Toaster position='top-center' reverseOrder={false}></Toaster>

    <div className='flex justify-center items-center h-screen'>
      <div className={styles.glass}  style={{ width: "45%", paddingTop: '3em'}}>

        <div className="title flex flex-col items-center">
          <h4 className='text-5xl font-bold'>Profile</h4>
          <span className='py-4 text-xl w-2/3 text-center text-gray-500'>
              You can update the details.
          </span>
        </div>

        <form className='py-1' onSubmit={formik.handleSubmit}>
            <div className='profile flex justify-center py-4'>
                <label htmlFor="profile">
                  <img src={ file || avatar} className={styles.profile_img}  alt="avatar" />
                </label>

                <input onChange={onUpload} type="file" id='profile' name='profile' />
            </div>

            <div className="textbox flex flex-col items-center gap-6">
              <div className="name flex w-3/4 gap-10">
                <input {...formik.getFieldProps('firstName')} className={styles.textbox} $ type="text" placeholder='FirstName' />
                <input {...formik.getFieldProps('lastName')} className={styles.textbox}  type="text" placeholder='LastName' />
              </div>

              <div className="name flex w-3/4 gap-10">
                <input {...formik.getFieldProps('mobile')} className={styles.textbox} type="text" placeholder='Mobile No.' />
                <input {...formik.getFieldProps('email')} classNam={styles.textbox} type="text" placeholder='Email*' />
              </div>


                <input {...formik.getFieldProps('address')} className={styles.textbox}  type="text" placeholder='Address' />
                <button className={styles.btn} type='submit'>Update</button>


            </div>

            <div className="text-center py-4">
              <span className='text-gray-500'>come back later? <button  className='text-red-500' to="/">Logout</button></span>
            </div>

        </form>

      </div>
    </div>
  </div>
    );
};

export default Profile;
